INSERT INTO `second_questions` VALUES (281,'﻿Чувствам се емоционално изтощен/а от моята работа.'),(282,'\r\nЧувствам се изчерпан/а в края на деня.'),(283,'\r\nЧувствам се изморен/а, когато трябва да стана сутрин и да посрещна още един работен ден.'),(284,'\r\nМога лесно да разбера за какво мислят колегите, с които работя.'),(285,'\r\nЧувствам, че не възприемам колегите си като отделни личности.'),(286,'\r\nРаботата с хора по цял ден е изтощение за мен.'),(287,'\r\nСправям се много успешно със задачите в работата ми.'),(288,'\r\nЧувствам се изпепелен от моята работа.'),(289,'\r\nУсещам, че правя разлика между отделните колеги в работата ми.'),(290,'\r\nСтанах по-безчувствен/а към хората, откакто работя в тази сфера.'),(291,'\r\nБезпокоят се, че от тази работа загрубявам.'),(292,'\r\nЧувствам се много енергичен/на.'),(293,'\r\nЧувствам се разочарован/а от моята работа.'),(294,'\r\nСтрува ми се, че работя с глямо усилие.'),(295,'\r\nНаистина не се интересувам какво се случва с някои хора, които срещам в работата си.'),(296,'\r\nПряката работа с хора предизвиква у мен твърде много напрежение.'),(297,'\r\nМога лесно да създам спокойна атмосфера при работата с колегита си.'),(298,'\r\nЧуствам се бодър/ра. След като съм работил/а в близък контакт с хора.'),(299,'\r\nВърша много ценни, значими неща в работата си.'),(300,'\r\nЧувствам се в края на силите си.'),(301,'\r\nВ работата си приемам емоционалните проблеми много спокойно.'),(302,'\r\nЧувствам, че колегите ми ме обвиняват за техните проблеми.');
